import "dhtmlx-gantt/codebase/dhtmlxgantt.css";
import gantt from "dhtmlx-gantt/codebase/dhtmlxgantt.js";
export default {
  mounted() {
    this.initGantt();
  },
  methods: {
    initGantt() {
      gantt.config.xml_date = "%Y-%m-%d %H:%i";

      // 创建第一个时间轴
      gantt.createTimeline({
        name: "timeline1",
        render: "bar",
        sections: [{
          key: "section1",
          label: "Timeline 1"
        }]
      });

      // 创建第二个时间轴
      gantt.createTimeline({
        name: "timeline2",
        render: "line",
        sections: [{
          key: "section2",
          label: "Timeline 2"
        }]
      });

      // 设置甘特图的任务数据
      gantt.init(this.$refs.ganttContainer);
      gantt.parse({
        data: [{
          id: 1,
          text: "Task 1",
          start_date: "2023-01-01",
          duration: 5,
          section_id: "section1",
          timeline: "timeline1"
        }, {
          id: 2,
          text: "Task 2",
          start_date: "2023-01-06",
          duration: 4,
          section_id: "section2",
          timeline: "timeline2"
        }, {
          id: 3,
          text: "Task 3",
          start_date: "2023-01-13",
          duration: 3,
          section_id: "section1",
          timeline: "timeline1"
        }, {
          id: 4,
          text: "Task 4",
          start_date: "2023-01-17",
          duration: 2,
          section_id: "section2",
          timeline: "timeline2"
        }],
        links: []
      });
    }
  }
};
