import "dhtmlx-gantt/codebase/dhtmlxgantt.css";
import gantt from "dhtmlx-gantt/codebase/dhtmlxgantt.js";
export default {
  name: "GanttComponent",
  data() {
    return {
      tasks: {
        data: [{
          id: 1,
          title: "一号里程碑",
          text: "一号里程碑",
          start_date: "2023-06-24 00:00",
          end_date: "2023-07-28",
          prottiem: "2023-06-24 00:00",
          propedtime: "2023-09-28",
          open: true,
          status: "进行中"
        }, {
          id: 2,
          title: "一号z里程碑",
          text: "一号z里程碑",
          start_date: "2023-06-24 00:00",
          end_date: "2023-08-28",
          prottiem: "2023-06-24 00:00",
          propedtime: "2023-09-28",
          open: true,
          parent: 1,
          status: "进行中"
        }]
      }
    };
  },
  mounted() {
    gantt.i18n.setLocale("cn");
    gantt.config.columns = [
    // 原始列
    {
      name: "title",
      label: "里程碑",
      width: "*",
      tree: true
    }, {
      name: "start_date",
      label: "开始日期",
      align: "center",
      width: 100
    }, {
      name: "status",
      label: "里程碑状态",
      width: 100
    }];
    gantt.templates.task_text = this.taskTextTemplate;
    gantt.templates.rightside_text = this.rightSideTextTemplate;
    gantt.plugins({
      marker: true,
      tooltip: true
    }); // 开启marker插件
    gantt.templates.tooltip_text = function (start, end, task) {
      return "<b>任务内容:</b>" + task.text + "<br/><b>计划开始:</b> " + task.start_date + "<br/><b>计划结束:</b> " + task.end_date + "<br/><b>实际开始:</b> " + task.prottiem + "<br/><b>实际结束:</b> " + task.propedtime;
    };
    // gantt.templates.grid_cell_status = function (item, column) {
    //   // 获取需要渲染的数据
    //   var value = item.status || "";

    //   // 根据值来渲染模板
    //   return "<div class='gantt_tree_content'>" + value + "</div>";
    // };
    gantt.config.drag_move = false; // 禁止用户移动任务
    gantt.config.xml_date = "%Y-%m-%d %H:%i";
    gantt.config.scale_unit = "year"; // 刻度单位为年
    gantt.config.date_scale = "%Y"; // 日期格式为年份
    gantt.config.scale_height = 50; // 刻度高度
    gantt.config.subscales = [{
      unit: "month",
      step: 1,
      date: "%m-%Y"
    }]; // 次刻度为月份和年份
    gantt.templates.task_date = function (start, end, task) {
      return gantt.date.date_to_str("%Y-%m")(start);
    };
    gantt.init(this.$refs.gantt);
    gantt.parse(this.tasks);
  },
  methods: {
    taskTextTemplate(start, end, task) {
      return `
      <div>计划开始</div>
    `;
    },
    rightSideTextTemplate(start, end, task) {
      console.log('start', start, end);
      // 返回实际开始和结束时间的进度条（上下排版）
      return `
      <div style="display: flex; flex-direction: column; margin-top: 5px;">
        <div style="display: flex; justify-content: space-between;">
          <div>计划开始: ${start}</div>
          <div>实际开始: ${task.actual_start_date}</div>
        </div>
        <div style="display: flex; justify-content: space-between;">
          <div>计划结束: ${end}</div>
          <div>实际结束: ${task.prottiem}</div>
        </div>
      </div>
    `;
    }
  }
};
