import { gantt } from 'dhtmlx-gantt';
import 'dhtmlx-gantt/codebase/dhtmlxgantt.css';
export default {
  name: 'GanttComponent',
  data() {
    return {
      tasks: {
        data: [{
          id: 1,
          text: "Project #1",
          start_date: "2023-06-24 00:00",
          duration: 3,
          progress: 0.6,
          open: true,
          children: [{
            id: 2,
            text: "Task #1",
            start_date: "2023-06-24 00:00",
            duration: 1,
            progress: 0.5
          }, {
            id: 3,
            text: "Task #2",
            start_date: "2023-06-25 00:00",
            duration: 2,
            progress: 0.3
          }, {
            id: 4,
            text: "Task #3",
            start_date: "2023-06-26 00:00",
            duration: 1,
            progress: 0.2
          }]
        }]
      }
    };
  },
  mounted() {
    gantt.plugins({
      // 拖动图
      drag_timeline: true,
      // 时间标记
      marker: true,
      // 全屏
      fullscreen: true,
      // 鼠标经过时信息
      tooltip: true,
      // 允许撤销
      undo: true
    });
    gantt.templates.scale_cell = function (date) {
      var formatFunc = gantt.date.date_to_str('%Y年%M月');
      return formatFunc(date);
    };
    // 修改日期格式为年月日时分秒
    gantt.config.date_format = "%Y年%m月%d日 %H:%i:%s";
    gantt.config.drag_move = false; //禁止拖动
    gantt.config.xml_date = "%Y-%m-%d %H:%i";
    gantt.config.subscales = [{
      unit: "month",
      step: 1,
      date: "%m-%Y"
    }]; // 次刻度为月份和年份
    gantt.init(this.$refs.gantt);
    gantt.parse(this.tasks);
  }
};
