//       const l = e.clientX - disX
//       const t = e.clientY - disY
//       // 计算移动当前元素的位置，并且给该元素样式中的left和top值赋值
//       target.style.left = l + 'px'
//       target.style.top = (t < minTop ? minTop : t) + 'px'
//       if (Math.abs(l - initX) > ifMoveSizeArea || Math.abs(t - initY) > ifMoveSizeArea) {
//         target.setAttribute('dragged', '')
//       } else {
//         target.removeAttribute('dragged')
//       }
//     }
//     document.onmouseup = e => {
//       document.onmousemove = null
//       document.onmouseup = null
//       document.onselectstart = null
//     }
//     // return false不加的话可能导致黏连，拖到一个地方时div粘在鼠标上不下来，相当于onmouseup失效
//     return false
//   }
// })

Vue.directive('drag', {
  bind(el, binding) {
    let startX, startY;
    let initialWidth, initialHeight;
    let originalCursor;
    let initialLeft, initialTop;
    
    const handleMouseMove = (event) => {
      const dx = event.clientX - startX;
      const dy = event.clientY - startY;

      let width = initialWidth;
      let height = initialHeight;
      let left = initialLeft + dx;
      let top = initialTop + dy;

      const { clientWidth: containerWidth, clientHeight: containerHeight } = el.parentNode;

      if (left + width > containerWidth) {
        left = containerWidth - width;
        el.style.borderRightColor = 'red';
      } else if (left < 0) {
        left = 0;
        el.style.borderLeftColor = 'red';
      } else {
        el.style.borderRightColor = '';
        el.style.borderLeftColor = '';
      }

      if (top + height > containerHeight) {
        top = containerHeight - height;
        el.style.borderBottomColor = 'red';
      } else if (top < 0) {
        top = 0;
        el.style.borderTopColor = 'red';
      } else {
        el.style.borderBottomColor = '';
        el.style.borderTopColor = '';
      }

      el.style.width = width + 'px';
      el.style.height = height + 'px';
      el.style.left = left + 'px';
      el.style.top = top + 'px';
    };

    const handleMouseUp = () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);

      el.style.cursor = originalCursor;
    };

    el.addEventListener('mousedown', (event) => {
      startX = event.clientX;
      startY = event.clientY;
      initialWidth = el.offsetWidth;
      initialHeight = el.offsetHeight;
      initialLeft = el.offsetLeft;
      initialTop = el.offsetTop;
      originalCursor = el.style.cursor;

      el.style.cursor = 'move';

      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
    });
  }
});

new Vue({
  router,
  store,
  i18n,
  render: h => h(App)
}).$mount('#app')
Vue.config.errorHandler = function(err) {
  Message.error("出错了");
};
