import "dhtmlx-gantt/codebase/dhtmlxgantt.css";
import Gantt from "dhtmlx-gantt/codebase/dhtmlxgantt.js";
export default {
  mounted() {
    this.initGantt();
  },
  methods: {
    initGantt() {
      Gantt.config.xml_date = '%Y-%m-%d';
      Gantt.config.columns = [{
        name: 'text',
        label: '任务名称',
        tree: true,
        width: '*'
      }, {
        name: 'start_date',
        label: '开始时间',
        align: 'center',
        width: '100'
      }, {
        name: 'end_date',
        label: '结束时间',
        align: 'center',
        width: '100'
      }, {
        name: 'duration',
        label: '持续时间',
        align: 'center',
        width: '100'
      }];
      Gantt.templates.task_class = function (start, end, task) {
        // 根据任务的类型来设置不同的样式
        if (task.type === 'milestone') {
          return 'milestone-task';
        }
        return '';
      };
      this.gantt = Gantt.getGanttInstance();
      this.gantt.init(this.$refs.ganttChartElement);
      this.gantt.parse({
        data: [{
          id: 1,
          text: '任务1',
          start_date: '2023-07-01',
          end_date: '2023-07-05',
          type: 'task'
        }, {
          id: 2,
          text: '里程碑1',
          start_date: '2023-07-06',
          type: 'milestone'
        }, {
          id: 3,
          text: '任务2',
          start_date: '2023-07-07',
          end_date: '2023-07-10',
          type: 'task'
        }],
        links: []
      });
      this.gantt.config.subscales = [{
        unit: 'day',
        step: 1,
        template: function (date) {
          return `<strong>${date.getDate()}</strong>`;
        }
      }, {
        unit: 'week',
        step: 1,
        template: function (date) {
          return `W${Gantt.date.date_to_str('%W')(date)}`;
        }
      }, {
        unit: 'month',
        step: 1,
        template: function (date) {
          return Gantt.date.date_to_str('%M')(date);
        }
      }];
      this.gantt.config.scale_unit = 'month';
      this.gantt.config.step = 1;
      this.gantt.config.date_scale = '%F, %Y';
      this.gantt.render();
    }
  }
};
