import { gantt } from 'dhtmlx-gantt';
import 'dhtmlx-gantt/codebase/dhtmlxgantt.css';
export default {
  name: 'GanttComponent',
  data() {
    return {
      tasks: {
        data: [{
          id: 1,
          text: "一号里程碑",
          start_date: "2023-06-24 00:00",
          duration: 3,
          progress: 0.6,
          open: true,
          children: [{
            id: 2,
            text: "Task #1",
            start_date: "2023-06-24 00:00",
            duration: 1,
            progress: 0.5
          }, {
            id: 3,
            text: "Task #2",
            start_date: "2023-06-25 00:00",
            duration: 2,
            progress: 0.3
          }, {
            id: 4,
            text: "Task #3",
            start_date: "2023-06-26 00:00",
            duration: 1,
            progress: 0.2
          }]
        }]
      }
    };
  },
  mounted() {
    // 获取 DOM 元素的引用
    const ganttContainer = this.$refs.gantt;
    gantt.templates.date_grid = gantt.date.date_to_str('%Y年%M月%D日');
    gantt.templates.date_scale = gantt.date.date_to_str('%Y年%M月%D日');
    // 设置 gantt 中文语言配置信息
    gantt.locale.labels.section_name = '名称';
    gantt.locale.labels.section_start_date = '开始日期';
    gantt.locale.labels.section_duration = '持续时间';
    gantt.locale.labels.section_add = '';
    gantt.locale.labels.section_progress = '进度';
    gantt.locale.labels.section_dependency = '前置任务';

    // 初始化 gantt 图表
    gantt.init(ganttContainer);

    // 设置 gantt 数据源
    gantt.parse(this.tasks);

    // ...
  },

  created() {
    // ...

    // 结构化所有任务（在数据中添加 `parent`、`open` 和 `duration` 字段）
    gantt.parse(this.tasks, function () {
      gantt.eachTask(function (task) {
        if (task.parent) {
          task.$open = true;
        } else {
          task.$open = false;
        }
        task.$duration = gantt.calculateDuration(task.start_date, task.end_date);
      });
    });

    // 平铺所有节点的父子关系
    gantt.eachTask(function (task) {
      task.$level = gantt.calculateTaskLevel(task);
    }, this);

    // ...
  }
};
