  mounted() {
    gantt.i18n.setLocale("cn");
    // gantt.config.columns = [
    //   // 原始列
    //   { name: "title", label: "里程碑", width: "*", tree: true },
    //   { name: "start_date", label: "开始日期", align: "center", width: 100 },
    //   { name: "status", label: "里程碑状态", width: 100 },
    // ];
    gantt.config.columns = [{
      name: "project_num",
      label: "项目编码",
      width: 80,
      align: "center"
    }, {
      name: "project_type",
      label: "项目类别",
      width: 80,
      align: "center"
    }, {
      name: "text",
      label: "项目名称",
      width: 100,
      align: "center"
    } // { name: "add", label: "" }, // ➕icon列显示 点击弹出添加框 隐藏没有
    ];

    gantt.templates.grid_cell_status = function (item, column) {
      // 获取需要渲染的数据
      var value = item.status || "";

      // 根据值来渲染模板
      return "<div class='gantt_tree_content'>" + value + "</div>";
    };
    gantt.config.drag_move = false; // 禁止用户移动任务
    gantt.config.xml_date = "%Y-%m-%d %H:%i";
    gantt.config.scale_unit = "year"; // 刻度单位为年
    gantt.config.date_scale = "%Y"; // 日期格式为年份
    gantt.config.scale_height = 50; // 刻度高度
    gantt.config.subscales = [{
      unit: "month",
      step: 1,
      date: "%m-%Y"
    }]; // 次刻度为月份和年份
    gantt.templates.task_date = function (start, end, task) {
      return gantt.date.date_to_str("%Y-%m")(start);
    };
    gantt.init(this.$refs.gantt);
    gantt.parse(this.tasks);
  }
};
