import Vue from 'vue'
import VueRouter from 'vue-router'

Vue.use(VueRouter)

const routes = [

  {
    path: '/home',
    name: 'home',
    meta: {
      title:"首页",
      requiresAuth: false, // 需要登录才能访问
      // roles: ['admin'] // 只有管理员角色可以访问
    },
    component: () => import("@/views/Home/Home.vue")
  },
  {
    path: '/user',
    name: 'user',
    meta: {
      title:"用户列表",
      requiresAuth: false, // 需要登录才能访问
      // roles: ['admin'] // 只有管理员角色可以访问
    },
    component: () => import("@/views/Home/Home.vue")
  }
]

const router = new VueRouter({
  routes
})

export default router
