import service from "@/utils/request";
import { Message } from "element-ui";
/*  请求超时处理 */
const Error = function(err){
    if(err.code === 'ECONNABORTED' || err.message ===   "Network Error" ||  err.message.includes("timeout")){  
        Message({
          message: '请求超时，请稍后刷新重试',
          type: "error",
          duration: 3 * 1000,
        })
      }
}
export function getyzm(data) {
    return service({
        url: "/user/y", // 这个地址是去掉公共地址剩下的地址
        method: "GET", // 请求方式 支持多种方式  get post put delete 等等
        data //发送请求要配置的参数 无参数的情况下也可以不写
    }).catch((err)=>{
        Error(err)
    });
    
}
