import Vue from 'vue'
import App from './App.vue'
import router from './router'
import store from '@/store/user'
import ElementUI, { Message } from 'element-ui';
import i18n from './lang/index'
import 'element-ui/lib/theme-chalk/index.css';
Vue.config.productionTip = false
Vue.use(ElementUI);
new Vue({
  router,
  store,
  i18n,
  render: h => h(App)
}).$mount('#app')
Vue.config.errorHandler = function(err) {
  Message.error("出错了");
};


Vue.directive('draggable', {
  inserted: function (el, binding) {
    const startX = 0, startY = 0;
    const initialX = 0, initialY = 0;

    const handle = document.createElement('div');
    handle.style.cssText = 'width: 10px; height: 10px; position: absolute; right: 0; bottom: 0; background-color: red; cursor: se-resize;';
    handle.addEventListener('mousedown', function (e) {
      initialX = e.clientX - initialX;
      initialY = e.clientY - initialY;
      startX = el.scrollLeft;
      startY = el.scrollTop;
      document.body.appendChild(handle);
      document.addEventListener('mousemove', drag);
      document.addEventListener('mouseup', stopDragging);
    });

    function drag(e) {
      const left = e.clientX - initialX + startX;
      const top = e.clientY - initialY + startY;
      el.style.position = 'absolute';
      el.style.left = `${left}px`;
      el.style.top = `${top}px`;
    }

    function stopDragging() {
      document.removeEventListener('mousemove', drag);
      document.removeEventListener('mouseup', stopDragging);
      document.body.removeChild(handle);
    }
  }
});
