    getDate(Atime, Btime, Ctime, Dtime) {
      let obj = {};
      let arr = [Atime, Btime, Ctime, Dtime];
      let timeArr = [];
      arr.forEach(item => {
        timeArr.push(new Date(item));
      });
      let minTime = moment(Math.min(...timeArr)).format("MM-DD-YYYY");
