import "dhtmlx-gantt";
import "dhtmlx-gantt/codebase/dhtmlxgantt.css";
export default {
  mounted() {
    // 渲染上部时间条
    this.renderGantt(this.$refs.topGanttContainer, this.topData);

    // 渲染下部时间条
    this.renderGantt(this.$refs.bottomGanttContainer, this.bottomData);
  },
  methods: {
    renderGantt(container, data) {
      gantt.init(container);

      // 设置时间条的配置选项，您可以根据您的需求进行自定义配置
      const config = {
        // 根据时间自定义渲染时间条
        // 在这里配置您的自定义渲染逻辑
        // 您可以使用gantt.templates.scale_cell中提供的时间格式化函数，以及样式设置来实现您的需求
        scales: [{
          unit: "month",
          step: 1,
          format: "%F, %Y"
        }, {
          unit: "day",
          step: 1,
          format: "%j %D"
        }]
      };

      // 渲染时间条
      gantt.parse({
        data,
        config
      });
    }
  },
  data() {
    return {
      // 上部时间条数据
      topData: [{
        id: 1,
        text: "Task 1",
        start_date: "2023-06-01",
        end_date: "2023-06-10"
      }
      // 其他任务对象...
      ],

      // 下部时间条数据
      bottomData: [{
        id: 1,
        text: "Task A",
        start_date: "2023-07-01",
        end_date: "2023-07-10"
      }
      // 其他任务对象...
      ]
    };
  }
};
