import Vue from 'vue'
import App from './App.vue'
import router from './router'
import store from '@/store/user'
import ElementUI, { Message } from 'element-ui';
import i18n from './lang/index'
import 'element-ui/lib/theme-chalk/index.css';
Vue.config.productionTip = false
Vue.use(ElementUI);
new Vue({
  router,
  store,
  i18n,
  render: h => h(App)
}).$mount('#app')
Vue.config.errorHandler = function(err) {
  Message.error("出错了");
};

Vue.directive('draggable', {
  bind: function (el, binding, vnode) {
    var initialMouseX = 0;
    var initialMouseY = 0;
    var initialValues = {
      top: 0,
      left: 0,
      width: 0,
      height: 0
    };
    var currentStyle = {};
    var updateStyle = function (event) {
      var currentX = event.clientX;
      var currentY = event.clientY;
      var diffX = currentX - initialMouseX;
      var diffY = currentY - initialMouseY;
      currentStyle.top = initialValues.top + diffY + 'px';
      currentStyle.left = initialValues.left + diffX + 'px';
      currentStyle.width = initialValues.width - Math.abs(diffX) + 'px';
      currentStyle.height = initialValues.height - Math.abs(diffY) + 'px';
      el.style.cssText = '';
      el.style.top = currentStyle.top;
      el.style.left = currentStyle.left;
      el.style.width = currentStyle.width;
      el.style.height = currentStyle.height;
    };
    el.addEventListener('mousedown', function (event) {
      initialMouseX = event.clientX;
      initialMouseY = event.clientY;
      initialValues.top = Number(el.style.top);
      initialValues.left = Number(el.style.left);
      initialValues.width = Number(el.style.width);
      initialValues.height = Number(el.style.height);
      document.addEventListener('mousemove', updateStyle);
    });
    document.addEventListener('mouseup', function () {
      document.removeEventListener('mousemove', updateStyle);
    });
  }
});
